efix(i,Mid,L),Q=Suffix(i+1,Mid+1,R),S=Mid-M-i;\n        int LB=max(1,S-Q),UB=min(P,S-1);if(LB<=UB)Ans+=UB-LB+1;\n      }\n  return Ans;\n}\n\nint main()\n{\n  int N,M;scanf(\"%d%d\",&N,&M);\n  for(int i=0;i<N;i++)scanf(\"%lld\",&A[i]);\n  for(int i=--N;i;i--)A[i]-=A[i-1];\n  Hash[0]=0;for(int i=1;i<=N;i++)Hash[i]=Hash[i-1]*Seed+A[i];\n  Pow[0]=1;for(int i=1;i<=N;i++)Pow[i]=Pow[i-1]*Seed;\n  printf(\"%d\n\",Solve(1,N,M));\n  return 0;\n}\n\n',1),(2256,739,'#include <ctime>\r\n#include <cstdio>\r\n#include <cstdlib>\r\n#include <cstring>\r\n#include <iostream>\r\nusing namespace std;\r\n\r\n#define MAXN 10010\r\n#define BLOCKSIZE 200\r\n#define BLOCKN 51\r\n#define MAXC 1000010\r\n\r\nint N, bN, M;\r\n\r\nint col[MAXN];\r\n\r\nint kc[BLOCKN][BLOCKN];\r\n\r\nstruct Node {\r\n	int pos;\r\n	int key;\r\n	Node *l, *r, *p;\r\n	Node *last, *next;\r\n} node[MAXN], *root[MAXC];\r\n\r\nvoid Zig (Node *x)\r\n{\r\n	Node *p = x->p;\r\n	Node *s = x->l;\r\n	\r\n	x->l = s->r;\r\n	if (s->r) s->r->p = x;\r\n	s->r = x;\r\n	s->p = p;\r\n	x->p = s;\r\n	if (p)\r\n	{\r\n		if (x == p->l) p->l = s;\r\n		else p->r = s;\r\n	}\r\n	else\r\n		root[col[x->pos]] = s;\r\n}\r\nvoid Zag (Node *x)\r\n{\r\n	Node *p = x->p;\r\n	Node *s = x->r;\r\n	\r\n	x->r = s->l;\r\n	if (s->l) s->l->p = x;\r\n	s->l = x;\r\n	s->p = p;\r\n	x->p = s;\r\n	if (p)\r\n	{\r\n		if (x == p->l) p->l = s;\r\n		else p->r = s;\r\n	}\r\n	else\r\n		root[col[x->pos]] = s;\r\n}\r\nvoid Insert (Node *x, Node *&p)\r\n{\r\n	if (!p)\r\n	{\r\n		p = x;\r\n		if (x->next) x->next->last = x;\r\n		if (x->last) x->last->next = x;\r\n		return;\r\n	}\r\n	if (x->pos < p->pos)\r\n	{\r\n		x->next = p;\r\n		x->p = p;\r\n		Insert (x, p->l);\r\n		if (p->key > p->l->key) Zig (p);\r\n	}\r\n	else\r\n	{\r\n		x->last = p;\r\n		x->p = p;\r\n		Insert (x, p->r);\r\n		if (p->key > p->r->key) Zag (p);\r\n	}\r\n}\r\nNode *Merge (Node *x, Node *y, Node *p)\r\n{\r\n	if (!x || !y) \r\n	{\r\n		if (x) {x->p = p; return x;}\r\n		else if (y) {y->p = p; return y;}\r\n		retur