		l = mid;\n				else\n					r = mid;\n			}\n		}\n		inline void Add(int s, int t)\n		{\n			S = s, T = t;\n			_Add(0, MaxiN - 1);\n		}\n};\n\nTInterval Tree;\n\nint main()\n{\n	scanf(\"%d\", &N);\n	For (i, N)\n		scanf(\"%d%d\", &Sail[i].x, &Sail[i].y);\n	sort(Sail, Sail + N);\n	For (i, N)\n	{\n		int j = Tree.Ask(Sail[i].x - Sail[i].y + 1);\n		int a = Tree.Aquire(j);\n		int b = Tree.Aquire(j + 1);\n		if (a < Sail[i].x)\n		{\n			Tree.Add(a, Sail[i].x);\n			Tree.Add(b, a + b - Sail[i].x + Sail[i].y);\n		}\n		else\n			Tree.Add(b, b + Sail[i].y);\n	}\n	For (i, MaxiN)\n	{\n		long long j = Tree.Ask(i + 1);\n		Ans += j * (j - 1) / 2LL;\n	}\n	cout << Ans << endl;\n}\n\n',1),(3055,1659,'#include <cstdio>\r\n#include <cstdlib>\r\n#include <algorithm>\r\nusing namespace std;\r\n\r\nconst int MAX=100005;\r\n\r\nint n;\r\nchar s[MAX+1];\r\nint dp[2][4][4][4];\r\n\r\nvoid read_input() {\r\n  scanf(\"%d\n\", &n);\r\n  fgets(s, MAX+1, stdin);\r\n  for (int i = 0; i < n; i++)\r\n    if (s[i] == \'F\')\r\n      s[i] = 1;\r\n    else if (s[i] == \'M\')\r\n      s[i] = 2;\r\n    else\r\n      s[i] = 3;\r\n}\r\n\r\ninline int ore (int a, int b, int c) {\r\n  int sol = 0;\r\n  if (a == 1 || b == 1 || c == 1) sol++;\r\n  if (a == 2 || b == 2 || c == 2) sol++;\r\n  if (a == 3 || b == 3 || c == 3) sol++;\r\n  return sol;\r\n}\r\n\r\nint main() {\r\n  read_input();\r\n\r\n  int now = 0, prev = 1;\r\n  for ( int k=n-1; k>=0; --k ) {\r\n     for ( int a=0; a<=3; ++a ) {\r\n        for ( int b=0; b<=3; ++b ) {\r\n           for ( int c=0; c<=3; ++c ) {\r\n              int left = ore(s[k], s[k-1], a) + dp[prev][s[k-1]][b][c];\r\n              int right = ore(s[k], b, c) + dp[prev][b][s[k-1]][a];\r\n              dp[now][a][b][c] = max(left, right);\r\n           }\r\n        }\r\n     }\r\n\r\n     swap(now, prev);\r\n  }\r\n  printf(\"%d\n\", dp[prev][0][0][0]);\r\n  return 0;\r\n}\r\n\n',1),(3056,1660,'\r\n// Program  :  CTSC 2010 galaxy\r\n\r\n#include <cstring>\r\n#include <cstdio>\r\n#define min(a, b) (((a) < (b)) ? (a) : (b))\r\n\r\nusing namespace std;\r\n\r\nstruct node\r\n  {\r\n    int data;\r\n    node *next;\r\n  } *ge[50000], edge[100000];\r\n\r\nint down[50000][2], up[50000][2], h[50000], pre[50000];\r\nint queue[50000], ans[50000], depth[50000], deg[50000];\r\nbool v[50000];\r\n\r\nvoid Ins_Edge(int a, int b)\r\n  {\r\n    static int top = 0;\r\n    node *p = &edge[top ++];\r\n    p->data = b, p->next = ge[a], ge[a] = p;\r\n  }\r\n\r\nint main()\r\n  {\r\n    int n, i, a, b, head, tail, now;\r\n    \r\n    scanf(\"%d\", &n);\r\n    for (i = 0; i < n; i ++) scanf(\"%d\", &h[i]);\r\n    for (i = 0; i < n; i ++) ge[i] = 0;\r\n    memset(deg, 0, sizeof(deg));\r\n    for (i = 1; i < n; i ++)\r\n      {\r\n        scanf(\"%d%d\", &a, &b), deg[a] ++, deg[b] ++;\r\n        Ins_Edge(a, b), Ins_Edge(b, a);\r\n      }\r\n    for (i = 0; i < n; i ++) h[i] -= deg[i];\r\n    \r\n    memset(v, false, sizeof(v));\r\n    v[0] = true, depth[0] = 0, queue[0] = 0;\r\n    for (head = 0, tail = 1; head < tail; head ++)\r\n      for (node *p = ge[queue[head]]; p; p = p->next)\r\n        if (! v[p->data])\r\n          {\r\n            pre[p->data] = queue[head];\r\n            depth[p->data] = depth[queue[head]] + 1;\r\n            v[p->data] = true, queue[tail ++] = p->data;\r\n          }\r\n    \r\n    memset(v, false, sizeof(v));\r\n    for (i = tail - 1; i >= 0; i --)\r\n      {\r\n        now = queue[i], v[now] = true;\r\n        down[now][0] = 0, down[now][1] = h[now];\r\n        for (node *p = ge[now]; p; p = p->next)\r\n          if (v[p->data])\r\n            {\r\n              down[now][0] += down[p->data][1];\r\n              down[now][1] += min(down[p->data][0], down[p->data][1]);\r\n            }\r\n      }\r\n    \r\n    ans[0] = min(down[0][0], down[0][1]);\r\n    for (i = 1; i < tail; i ++)\r\n      {\r\n        now = queue[i];\r\n        up[now][0] = down[pre[now]][0] - down[now][1];\r\n        up[now][1] = down[pre[now]][1] - min(down[now][0], down[now][1]);\r\n        if (pre[now] != 0)\r\n          {\r\n            up[now][0] += up[pre[now]][1];\r\n            up[now][1] += min(up[pre[now]][0], up[pre[now]][1]);\r\n            up[now][1] ++;\r\n          }\r\n        ans[now] = down[now][1] + 1 + min(up[now][0], up[now][1]);\r\n        ans[now] = min(ans[now], down[now][0] + up[now][1]);\r\n      }\r\n    \r\n    for (i = 0; i < n; i ++)\r\n      printf(\"%d\n\", ans[i] * 2 - depth[i] + 2 * (n - 1));\r\n    \r\n    return 0;\r\n  }\r\n\n',1),(3057,1661,'#include <algorithm>\r\n#include <cstdio>\r\n\r\ntypedef long long llint;\r\n\r\nusing namespace std;\r\n\r\nstruct loga3d {\r\n   int a[225*225*225];\r\n   int X, Y, Z;\r\n\r\n   inline int & polje( int x, int y, int z ) {\r\n      return a[ ((x-1)*Y + (y-1))*Z + (z-1) ];\r\n   }\r\n\r\n   int query( int x0, int y0, int z0 ) {\r\n      int ret = 0;\r\n      for( int x = x0; x > 0; x -= x&-x )\r\n         for( int y = y0; y > 0; y -= y&-y )\r\n            for( int z = z0; z > 0; z -= z&-z )\r\n               ret += polje( x, y, z );\r\n      return ret;\r\n   }\r\n\r\n   int query( int x1, int x2, int y1, int y2, int z1, int z2 ) {\r\n      --x1; x1 =max(x1, 0); x2 =min(x2, X);\r\n      --y1; y1 =max(y1, 0); y2 =min(y2, Y);\r\n      --z1; z1 =max(z1, 0); z2 =min(z2, Z);\r\n\r\n      return query( x2, y2, z2 ) \r\n         - query( x1, y2, z2 ) - query( x2, y1, z2 ) - query( x2, y2, z1 )\r\n         + query( x1, y1, z2 ) + query( x1, y2, z1 ) + query( x2, y1, z1 )\r\n         - query( x1, y1, z1 );\r\n   }\r\n\r\n   void update( int x0, int y0, int z0, int val ) {\r\n      for( int x = x0; x <= X; x += x&-x )\r\n         for( int y = y0; y <= Y; y += y&-y )\r\n            for( int z = z0; z <= Z; z += z&-z )\r\n               polje( x, y, z ) += val;\r\n   }\r\n};\r\n\r\nloga3d L;\r\nint B, N, K, M;\r\n\r\nstruct point {\r\n   int x, y, z;\r\n   int a, b, c, d;\r\n} tocke[100000];\r\n\r\nbool operator < ( const point &P, const point &Q ) { return P.a < Q.a; }\r\n \r\nint main( void ) {\r\n   scanf( \"%d%d%d%d\", &B, &N, &K, &M );\r\n\r\n   if( B == 1 ) { L.X = 1;   L.Y = 1;   L.Z = 1;   }\r\n   if( B == 2 ) { L.X = 2*M; L.Y = 1;   L.Z = 1;   }\r\n   if( B == 3 ) { L.X = 3*M; L.Y = 3*M; L.Z = 3*M; }\r\n\r\n   for( int i = 0; i < N; ++i ) {\r\n      point T;\r\n      if( B == 1 ) {\r\n         scanf( \"%d\", &T.x ); \r\n         T.a = T.x; \r\n         T.b = 1;  \r\n         T.c = 1; \r\n         T.d = 1;\r\n      }\r\n      if( B == 2 ) {\r\n         scanf( \"%d%d\", &T.x, &T.y ); \r\n         T.a = T.x+T.y - 1;\r\n         T.b = T.x-T.y + M;\r\n         T.c = 1; \r\n         T.d = 1;\r\n      }\r\n      if( B == 3 ) {\r\n         scanf( \"%d%d%d\", &T.x, &T.y, &T.z );\r\n         T.a = T.x+T.y+T.z - 2;\r\n         T.b = T.x+T.y-T.z + M-1;\r\n         T.c = T.x-T.y+T.z + M-1;\r\n         T.d = T.x-T.y-T.z + M+M;\r\n      }\r\n      tocke[i] = T;\r\n   }\r\n   sort( tocke, tocke+N ); \r\n\r\n   llint ret = 0;\r\n   int head = 0, tail = 0;\r\n   while( head < N ) {\r\n      while( tocke[head].a - tocke[tail].a > K ) \r\n         L.update( tocke[tail].b, tocke[tail].c, tocke[tail].d, -1 ), ++tail;\r\n\r\n      ret += L.query(  tocke[head].b-K, tocke[head].b+K,\r\n                       tocke[head].c-K, tocke[head].c+K,\r\n                       tocke[head].d-K, tocke[head].d+K );\r\n\r\n      L.update( tocke[head].b, tocke[head].c, tocke[head].d, 1 ), ++head;\r\n   }\r\n\r\n   printf( \"%I64d\n\", ret );\r\n\r\n   return 0;\r\n}\r\n\n',1),(3058,1662,'\r\n// Program  :  CTSC 2010 go\r\n\r\n#include <cstdio>\r\n\r\nusing namespace std;\r\n\r\ndouble f[1<<18][63], p[21][21];\r\nint lg[1<<18];\r\n\r\nint main()\r\n  {\r\n    int s[3], t[3], n, i, j, k, t1, t2, tmp;\r\n    int full, cur, loc, last, next;\r\n    double val, ans = 0;\r\n    \r\n    scanf(\"%d\", &n);\r\n    for (t1 = 0; t1 < 3; t1 ++)\r\n      for (t2 = t1 + 1; t2 < 3; t2 ++)\r\n        for (i = 0; i < n; i ++)\r\n          for (j = 0; j < n; j ++)\r\n            {\r\n              int loc1 = i * 3 + t1, loc2 = j * 3 + t2;\r\n              scanf(\"%lf\", &p[loc1][loc2]);\r\n              p[loc2][loc1] = 1 - p[loc1][loc2];\r\n            }\r\n    \r\n    full = (1 << (3 * n - 3)) - 1;\r\n    for (lg[1] = 0, i = 2; i <= full; i ++)\r\n      lg[i] = lg[i-1] + ((i & -i) == i);\r\n    t[0] = t[1] = t[2] = 0;\r\n    for (i = 0; i < 3 * n - 3; i ++)\r\n      t[i%3] += 1 << i;\r\n    \r\n    for (i = 1; i <= full; i ++)\r\n      for (j = 0; j < n * 9; j ++)\r\n        {\r\n          f[i][j] = 0;\r\n          s[0] = i & t[0], s[1] = i & t[1], s[2] = i & t[2];\r\n          cur = j / 3, last = j % 3;\r\n          t1 = cur % 3, t2 = 3 - last - t1;\r\n          if (t1 == last || s[0] == 0) continue;\r\n          if (cur / 3 < n - 1 && ! (i & 1 << cur)) continue;\r\n          if (s[1] == 0 && s[2] == 0)\r\n            {\r\n              f[i][j] = 1;\r\n              continue;\r\n            }\r\n          if (s[t2] == 0) t2 = last;\r\n          if (t2 != 0) f[i][j] = 1e100;\r\n          for (tmp = s[t2]; tmp; tmp -= tmp & -tmp)\r\n            {\r\n              loc = lg[tmp&-tmp], next = i - (1 << loc);\r\n              val = p[cur][loc] * f[next][cur*3+t2];\r\n              if (next = i, cur / 3 < n - 1) next -= 1 << cur;\r\n              val += p[loc][cur] * f[next][loc*3+t1];\r\n              if (t2 == 0 && val > f[i][j]) f[i][j] = val;\r\n              if (t2 != 0 && val < f[i][j]) f[i][j] = val;\r\n            }\r\n        }\r\n    \r\n    for (i = 0; i < 3; i ++)\r\n      for (j = 0; j < 3; j ++)\r\n        if (i != j)\r\n          {\r\n            k = 3 - i - j;\r\n            val = p[(n-1)*3+i][(n-1)*3+j] * p[(n-1)*3+j][(n-1)*3+k] / 3;\r\n            val += p[(n-1)*3+i][(n-1)*3+j] * p[(n-1)*3+i][(n-1)*3+k] / 3;\r\n            ans += val * f[full][((n-1)*3+i)*3+j];\r\n          }\r\n    \r\n    printf(\"%.6lf\n\", ans);\r\n    \r\n    return 0;\r\n  }\r\n\n',1),(3059,1663,'#include <algorithm>\r\n#include <cstdio>\r\n#include <vecto